package FizzBuzz;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;

import static org.junit.jupiter.api.Assertions.assertEquals;


public class FizzBuzzTest {
	
	FizzBuzzer fizzBuzzer = new FizzBuzzer();

	@ParameterizedTest 
    @CsvSource(
    		{"15, fizzbuzz",
            "3, fizz",
            "4, 4",
            "5, buzz",
            "6, fizz",
            "7, 7",
            "8, 8",
            "9, fizz",
            "10, buzz",
            "15, fizzbuzz",
            "20, buzz",
            "30, fizzbuzz",
            "45, fizzbuzz"
            }
        )
    public void WhenAMultipleOf3IsPassedItReturnsFizz(int in, String out) {
        assertEquals(out, fizzBuzzer.fizzBuzz(in));
        
    }
    
    List<String> stringOutput = new ArrayList<String>(Arrays.asList( "1","2","fizz","4","buzz","fizz", "7","8", "fizz", "buzz", "11", "fizz", "13", "14", "fizzbuzz",
    		 "16","17","fizz","19","buzz","fizz", "22","23", "fizz", "buzz", "26", "fizz", "28", "29", "fizzbuzz")); 
    
    @Test
    public void WriteTheWholeSequenceOfFizzBuzzerUntilTheProvidedNumber() {
    	assertEquals(stringOutput, fizzBuzzer.fizzBuzzer(30));
    }
    
}
